// This function takes the stringified JSON from the LLM,
// cleans it up, and parses it into a usable JSON object.
// It is specifically designed to handle the `story` field being an array of two strings.

const items = $input.all(); // Get all input items from the previous node
const resultItems = [];

for (const item of items) {
  let jsonString;

  // --- Step 1: Extract the JSON string from the n8n input structure ---
  // This logic correctly finds the stringified JSON regardless of n8n's output nesting.
  if (typeof item.json.output === 'string') {
    jsonString = item.json.output;
  } else if (Array.isArray(item.json) && item.json.length > 0 && typeof item.json[0].output === 'string') {
    jsonString = item.json[0].output;
  } else {
    // If the structure is unexpected, log an error and skip this item.
    console.error("Unexpected input structure from LLM:", JSON.stringify(item.json));
    resultItems.push({ json: { error: "Unexpected input structure from LLM", originalData: item.json } });
    continue;
  }

  // --- Step 2: Clean and Parse the stringified JSON ---
  try {
    // The LLM often adds newline characters (`\n`) for readability, which are not valid in the middle
    // of a JSON string unless they are inside a quoted value. We can remove the most common ones.
    // NOTE: This is a targeted replacement. If the LLM puts `\n` *inside* the story text, this might remove it.
    // However, for cleaning up the overall structure, this is generally safe.
    // A better approach is to remove them only from between JSON structural elements, but that's more complex.
    // Let's stick to a robust but simpler cleaning method first.

    // Replace escaped newlines `\n` and escaped tabs `\t` with nothing, effectively removing them.
    let cleanedString = jsonString.replace(/\\n/g, '').replace(/\\t/g, '');

    // Now, parse the cleaned string into a JavaScript object.
    const parsedJson = JSON.parse(cleanedString);

    // --- Step 3: (Optional but Recommended) Final cleanup of story strings ---
    // Sometimes, after parsing, the strings inside the story array might have leading/trailing whitespace.
    if (parsedJson.story && Array.isArray(parsedJson.story)) {
      parsedJson.story = parsedJson.story.map(part => part.trim());
    }

    resultItems.push({ json: parsedJson });

  } catch (error) {
    // If parsing fails, log the error and the problematic string for debugging.
    console.error("Error parsing JSON string:", error);
    console.error("Problematic string after initial cleanup attempt:", jsonString);
    resultItems.push({ json: { error: "Failed to parse JSON from LLM", originalString: jsonString } });
  }
}

return resultItems;

const ffprobeJsonString = $input.item.json.stdout;
let durationSeconds = null;

try {
  const ffprobeData = JSON.parse(ffprobeJsonString);

  // Duration can be in 'format.duration' or 'streams[audioStreamIndex].duration'
  // 'format.duration' is usually reliable for single files.
  if (ffprobeData.format && ffprobeData.format.duration) {
    durationSeconds = parseFloat(ffprobeData.format.duration);
  } else if (ffprobeData.streams && ffprobeData.streams.length > 0) {
    // Find the first audio stream and use its duration
    const audioStream = ffprobeData.streams.find(stream => stream.codec_type === 'audio');
    if (audioStream && audioStream.duration) {
      durationSeconds = parseFloat(audioStream.duration);
    }
  }

  if (durationSeconds !== null) {
    return {
      json: {
        duration: durationSeconds,
        duration_formatted: new Date(durationSeconds * 1000).toISOString().substr(11, 8) // HH:MM:SS
        // You can also return the full ffprobeData if needed
        // ffprobe_details: ffprobeData
      }
    };
  } else {
    // If duration couldn't be found
    return {
      json: {
        error: "Could not extract duration from ffprobe output.",
        ffprobe_output: ffprobeData // return the data for debugging
      }
    };
  }

} catch (error) {
  console.error("Error parsing ffprobe JSON:", error);
  console.error("FFprobe stdout:", ffprobeJsonString);
  return {
    json: {
      error: "Failed to parse ffprobe JSON output.",
      raw_stdout: ffprobeJsonString
    }
  };
}

// Get the JSON data from the previous node (e.g., "Format Script" or your LLM parser node)
const data = $('Format Script').first().json;

// Extract the relevant parts
const storyParts = data.story; // This is the array, e.g., ["Part 1 text", "Part 2 text"]
const title = data.title;
const voice = data.voice;
const audioPath = $input.first().json.audio_path; // Assuming this comes from an earlier node

// --- Critical Change Here ---
// Prepend the title and a space to the *first element* of the story array.
if (storyParts && storyParts.length > 0) {
  storyParts[0] = title + " " + storyParts[0];
}

// Start building the command string with the static parts
let command = `python3 /opt/scripts/a4f-local.py --output "${audioPath}.mp3" --voice "${voice.toLowerCase()}"`;

// Loop through the now-modified storyParts array
for (const part of storyParts) {
    // This escaping function is crucial and correct for handling single quotes in shell commands
    const escapedPart = part.replace(/'/g, "'\\''");

    // Append each part as a separate --text argument
    command += ` --text '${escapedPart}'`;
}

// Return the final command in the format the "Execute Command" node expects
return {
  command: command
};

